import 'package:bookly/Config/Routes/next_screen.dart';import 'package:bookly/Config/Routes/route_app.dart';import 'package:bookly/Core/Utils/Resources/text_style.dart';import 'package:bookly/Feature/Home/presentation/manager/MainCubit/main_cubit.dart';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';class CustomBookDetailsAppbar extends StatelessWidget {  const CustomBookDetailsAppbar({    super.key,  });  @override  Widget build(BuildContext context) {    return BlocBuilder<MainCubit, MainState>(  builder: (context, state) {    return Padding(      padding: const EdgeInsets.only(left: 20, right: 20, bottom: 0, top: 20),      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        children: [          IconButton(            onPressed: () => popScreen(              context,            ),            icon: const Icon(Icons.close_sharp),          ),          Stack(            alignment: Alignment.topRight,            children: [              IconButton(                onPressed: () => pushNext(context, AppRoutes.basketRoute),                icon: const Icon(Icons.shopping_cart_outlined),              ),              Text(                '${MainCubit.get(context).listOfBookMapsLen}',                style: Styles.textStyle14.copyWith(color: Colors.yellow),              )            ],          ),        ],      ),    );  },);  }}