import 'package:bookly/Core/Utils/Widget/custom_loading.dart';import 'package:bookly/Core/Utils/Widget/custom_massage.dart';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../../../../Core/Utils/Widget/View_Books/build_items_books_horizontal.dart';import '../../../../../Core/Utils/Resources/text_style.dart';import '../../manager/similar_books_cubit.dart';import '../../manager/similar_books_state.dart';const String _title = "Similar Books";class SimilarBooks extends StatelessWidget {  final String ? id ;  const SimilarBooks({super.key, this.id});  @override  Widget build(BuildContext context) {    return BlocBuilder<SimilarBooksCubit, SimilarBooksState>(      builder: (context, state) {        if (state is SimilarBooksSuccess) {          return _body(context, state);        } else if (state is SimilarBooksFailure) {          return Expanded(child: CustomMassage(massage: state.error));        } else {          return const Expanded(child: CustomLoading());        }      },    );  }  Expanded _body(BuildContext context, SimilarBooksSuccess state) {    return Expanded(      child: Center(        child: SingleChildScrollView(          child: Padding(            padding: const EdgeInsets.only(left: 30),            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                Padding(                  padding: const EdgeInsets.only(top: 20, left: 35, bottom: 10),                  child: Text(                    _title,                    style: Styles.textStyle18                        .copyWith(fontWeight: FontWeight.w700),                  ),                ),                if (state.books.isNotEmpty)                  SizedBox(                      height: MediaQuery.of(context).size.height * .20,                      child: BuildItemsBooksHorizontal(                        id: id ,                        items: state.books,                      ))              ],            ),          ),        ),      ),    );  }}